import HoverCard from "./HoverCard";
import "./HoverCard.css";
import React from "react";
import bike from './items/bike.jpg'
import kenny from "./kenny-chi.jpeg";
import iris from "./iris-dong.jpeg";


function HoverCards(props) {
    let data = [
        {id:'item1', bottomText: 'Hi everyone, my name is Kenny Chi and I\'m a second year CS student at UC Berkeley', image: kenny, description: 'left'},
        {id: 'item2', bottomText: 'Hi everyone, my name is Iris Dong and I\'m a second year ECE student at Carnegie Mellon University', image: iris, description: 'right'},
    ]
    let cards = []
    for (let i = 0; i < data.length; i++) {
        cards.push(<HoverCard id={data[i].id} imageURL={data[i].image} bottomText={data[i].bottomText} centerText={data[i].description} size={{width: "400px", height: "400px"}}/>)
    }

    return(
            <div className = 'findpage'>
                <button className='button1'>
                    Only Nearby Stores
                </button>
                <div className="hover-card-demo">{cards}</div>
            </div>


    )
}

export default HoverCards;
